import React from 'react'
import { Row, Col, Avatar, Form, Input, Radio, Select, Button, Divider, Card } from "antd";
import "antd/dist/antd.css";
import { _validnumber } from '../../helpers/index';
import { url } from '../../../API/url'
import { token } from '../../../API/token'
import Header from '../Header/index'

const { TextArea } = Input;
const { Option } = Select;
const { Meta } = Card;

export default class DashBoard extends React.Component {

    constructor(props) {
        super(props)

        this.state = {
            books: []
        };
    }

    componentDidMount() {
        this.fetchData()
    }

    fetchData = () => {
        fetch(url + 'books', {
            method: 'get',
            headers: {
                "Authorization": token,
                "Content-Type": "application/json"
            }
        })
            .then(response => response.json())
            .then(data => (
                data.data.forEach(x => {
                    x.categoryId = x.bookList[0]._id
                }),
                this.setState({
                    books: data.data,
                }),
                console.log('getBook API ', this.state.books)
            ))
    }

    render() {
        const { books } = this.state
        const bookList = books.map(x => {
            <div key={x._id}>
                <Col span={6} >
                    <Card
                        hoverable
                        style={{ width: 240 }}
                        cover={<img alt="example" src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" />}
                    >
                        <Meta title="Europe Street beat" description="www.instagram.com" />
                    </Card>
                </Col>
            </div>
        })
        return (
            <div>
                <div>
                    <Header />
                </div>
                <div style={{ textAlign: 'center', marginTop: '30px' }}>
                    <Row>
                        {bookList}
                    </Row>
                </div>
            </div>
        )
    }
}
